css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {
  ul.bookmarks li.post .meta {padding-left: 0 !important; margin-left: 0 !important;}
  ul.bookmarks li.post .meta .saverem, ul.bookmarks li.post .meta .user-tag {margin-left: 2em !important; float: right !important;}
  ul.bookmarks li.post .tagdisplay {float: none !important;}
  ul.bookmarks li.post .tag-chain {float: none !important; text-align: left !important; margin-right: 14em !important;}
  ul.bookmarks li.post .tag-chain-label {display: none !important;}
}
