css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("channelawesome.com") {

/* header area*/
  
.logo {
  float: none;
  text-align: center;
}
  
.header-content {
  background: none;
}
  
/* paging */
  
#flexslider ol.flex-control-paging li a {
  width: 16px;
  height: 16px;
  border: solid 1px #fff;
  opacity: 0.8;
  text-indent: 0px;
  line-height: 15px;
  vertical-align: middle;
  text-align: center;
  font-weight: bold;
  font-size: 10px;
  font-family: Oswald, verdana;
  border-radius: 0px;
  margin-right: 4px;
}
  
#flexslider .flex-control-paging li a:hover {
  opacity: 1;
}

#flexslider .flex-control-paging {
  left:5px;
  top: 5px;
}

/* next - prev */
  
#flexslider .flex-direction-nav {
  position: absolute;
  left: 0;
  top: 50px;
  bottom: 50px;
  width: 100%;
  /* for shadows; if any*/
  overflow: hidden;
  pointer-events: none;
}
  
#flexslider .flex-direction-nav li {
  position: absolute;
  width: 40px;
  height: 100%;
  display: block !important;
  
  vertical-align: middle;
  pointer-events: all;
}
  
#flexslider .flex-direction-nav li:first-of-type {
  left: 0px;
}
  
#flexslider .flex-direction-nav li:last-of-type {
  right: 0px;
}

#flexslider .flex-direction-nav li a {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  text-align: center;
  background:none;
}
  
#flexslider .flex-direction-nav li a:before {
  position: absolute;
  left: 0;
  top: 50%;
  margin-top: -25px;
  vertical-align: middle;
  line-height: 50px;
  text-align: center;
  width: 100%;
}
  
}
