css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reverso.net") {

/******************************/
/*   Reverso.net - Clean up   */
/******************************/
/*     Updated by loxaxs      */
/* from the version of walrus */
/******************************/
/* 2.1 Update: sept 2016 */

#footer,
.gogad,
#headerHome,
.columnBanner2,
.box2,
#leftColumn > div:not(.box1),
#newsBox,
#suggest_but1,
.box3,
#rightColumn > div:not(.box1):not(#new_edit_buttons),
.sayBox>a,
#rightColumn #fbDiv,
#content #rightcom,
.banner,
.bottom-banner,
#iFrameLOCDLatest,
#contextsection > div:not(.context-more),
.lBoxH,
.message-wrap--powered,
.left-part, .discover-block
{
display: none !important;
}

  
.headerDescription,
.utilizer,
.third-column
  {
    display: none !important;
  }
  
.wrapper-form-bg1,
.wrapper-form-bg-2,
.wrapper-form-bg-3
  {
    background-image: none !important;
  }
  
.rightColumn
  {
    float: none !important;
  }
  
.w404
  {
    width: inherit !important;
  }

.box1
  {
    text-align: center;
  }

.dirsection
  {
    position: relative;
    left: calc(50% - 16em);
  }
  
}
