css:
@-moz-document url-prefix("http://www.sweclockers.com"), url-prefix("https://www.sweclockers.com") {
/* Header and logo */

h5 { margin-bottom: 0px; }

* .header .sections>.section {
  margin-top: 2px !important;
  padding-top: 0px !important;
  height: 66px !important;
  margin-bottom: 0px !important;
  padding-bottom: 0px !important;
}

.header>.main {
	min-height: 92px;
}

* .header .sections {
  height: 68px;
}

* .pushList .plHead {
  padding-top: 6px !important;
  margin: 0 6px 0 6px !important;
}

* .pushList .plBody {
	margin: 0 6px 0 12px !important;
  
}

* .pushList .plFoot {
	height: 6px;
}

* .pushList .plItem {
	margin: 0px;
}

* .pushList .plLabel>.plLabelText {
	max-width: 237px;
}



/* Main and News page */
.mainContent>.inner {
	padding: 12px 16px 12px 5px;
}

.newsList .pubItem {
	margin: 0 0 10px 0;
}



/* Forum inner header */
.whiteHeader {
	margin-top: 5px;
	margin-bottom: 5px;
}

.newsList .itemBanner {
	margin-bottom: 2px;
}

.pubItem.halfBanner .itemBanner>.inner {
	margin-right: 2px !important;
}

.newsList .pubItem.halfBanner .itemBanner {
	width: auto;
}

.pubItem.halfBanner .itemBody {
	width: 58% !important;
}

/*  Forum list */
.forumTable .caption {
	padding: 0px 18px 0px;
}

.forumTable .table .head .cell {
	padding: 1px 1px;
}


.forumTable .table .cell {
  padding: 2px 7px;   
}

.threadList .dateline {
  font-size: 10px;
  height: 13px;
}

.forumTable h2 {
  line-height: 1;
}

.threadList .pageLinks {
  height: 13px;
}

.threadList .pageLinks .link {
	font-size: 10px;
	vertical-align: top;
}


.forumList .cell.status {
	padding: 0px 5px 0px 5px !important;
}

/* Forum navigation */

.pages {
	margin-bottom: 5px;
}


/* Footer news etc. */
.mainWidget {
	padding-left: 5px;
	padding-right: 5px;
	margin-bottom: 5px;
}

.mainWidget .mwHead {
	padding-top: 5px;
}

.mainWidget .mwItem {
	padding-top: 0px;
 	padding-bottom: 0px;
}

.mainWidget .mwItem.mwItemDivider { 
	margin-top: 0px;
	padding-top: 0px;
	border-top-width: 0px;
	border-top-style: solid;
}

.mainWidget .mwItemList {
	margin-bottom: 5px;
}

.mainWidget .mwFoot {
	height: 5px;
}

/* Footer, other */
.footer .top {
	padding: 0 0 0;
}

.footer .top .inner {
	padding: 3px 5px;
}

.footer .top .block {
	height: 160px;
}

.footer .bottom {
	padding: 5px 0 5px 0;
}
}
