css:
@-moz-document domain(vozforums.com) {

/* Banh rộng  + xóa khung phải +  mở rộng tránh tên nick đụng time, bỏ scrollbar ngang) */
.page {min-width: 99%; background: #5C7099} body {margin:auto} TD[width="160"] {display:none}
[style*=wrap] > STRONG {margin-right:100px} html {overflow-x:hidden}
  
/* Canh giữa hình, test: http://vozforums.com/showthread.php?t=2663169 */
[id*="ncode_imageresizer"],[id^="attachment"],
div[id*="post_message_"] [onload="NcodeImageResizer.createOn(this);"]:not([src*="vozforums.com/images/smilies"])
{margin-left: auto !Important; margin-right: auto !important; display:table !important}
  
/* Fix vụ vỡ bố cục trang khi mở ảnh to , test : http://vozforums.com/showthread.php?t=3687819 */
.alt1 *  {margin-right: auto ;margin-left:auto}
  
/* Làm màu khung Reply */
.panel , .fieldset, .vBulletin_editor, .button, [id$=smiliebox] {background: #E4E7F5 !important; border:none !important}
.button {height: 30px} .button:hover, .imagebutton * {background: #F2F2FF !important} 
 
/* Banh rộng khung soạn thảo */
DIV[style="width:640px"], [type*="javascript"] + TABLE, #vB_Editor_001_textarea {min-width:100% }

}
