css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-tabs > tab .tab-close-button {
margin: 1px 0 3px !important;
}

.tabbrowser-tabs > tab .tab-icon {
margin: 0 !important;
}

.tabbrowser-tabs {
background: transparent !important;
margin-top: 1px !important;
}

.tabbrowser-tabs .tabs-bottom {
background: #D4D0C8 !important;
border-top: 0 !important;
height: 0 !important;
}

.tabbrowser-tabs .tabs-frame {
margin-top: 2px !important;
}

.tabbrowser-tabs .tabbrowser-tab[selected=true] {
margin-bottom: 0px !important;
}

.tabbrowser-tabs .tabbrowser-tab {
margin-left: 1px !important;
margin-bottom: -1px !important;
background: transparent !important;
padding-top: 0 !important;
}

.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-middle,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-image-left,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-image-right {
background: #F0F0E8 !important;
border: 1px solid #666666 !important;
}

.tabbrowser-tabs tab:hover .tab-middle,
.tabbrowser-tabs tab:hover .tab-image-left,
.tabbrowser-tabs tab:hover .tab-image-right
{
background: #E4E0D8 !important;
}

.tabbrowser-tabs .tab-middle,
.tabbrowser-tabs .tab-image-left,
.tabbrowser-tabs .tab-image-right
{
background: #D9D4D0 !important;
border: 1px solid #96969D !important;
}

.tabbrowser-tabs .tab-middle,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-middle {
border-left: 0 !important;
border-right: 0 !important;
}
.tabbrowser-tabs .tab-image-left,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-image-left {
border-right: 0 !important;
}
.tabbrowser-tabs .tab-image-right,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-image-right {
border-left: 0 !important;
}

.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-middle,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-image-left,
.tabbrowser-tabs .tabbrowser-tab[selected=true] .tab-image-right {
border-top: 2px solid #6FA2E4 !important;
}
