css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://google.com/notebook), url-prefix(http://google.ca/notebook) {

body { font-family: Arial, sans serif ! important; margin: 0 ! important; background: #E4E4EE ! important; }

td a:link, a:visited, a:active { text-decoration: none ! important; color: #000000 ! important; }

#page_footer { display: none ! important; }

table td font { visibility: hidden ! important; }

[href*="Logout"] { visibility: visible ! important;  position: absolute ! important; top: 6px ! important; right: 6px ! important;  }

td[valign="top"] { visibility: hidden ! important; }

td[width="100%"] { visibility: visible ! important; margin-left: 10px ! important;  }

#search_box { position: absolute ! important; top: 6px ! important; left: 6px ! important; }

#search_submit { visibility: visible ! important; position: absolute ! important; top: 6px ! important; left: 280px ! important; }

.NotificationArea { display: none ! important; }

.TrashNameInOverview { text-decoration: none ! important; color: #000000 ! important; }

.NotebookNameInOverview { text-decoration: none ! important; color: #000000 ! important; }

.RefreshAction { text-decoration: none ! important; color: #000000 ! important; }

.SharingOptionsLink { text-decoration: none ! important; color: #000000 ! important; }

.AddNoteSectionText { text-decoration: none ! important; color: #000000 ! important; }

[href*="/notebook/"] { display: none ! important; }

.Overview { background: #E4E4EE ! important; }

.UnpublishedPrimary { background: #7E7FA0 ! important; }

#detailview_8 { background: #7E7FA0 ! important; border: 0px ! important; }

.AddNoteSectionWrapper { display: none ! important; }

#detailview_9 { display: none ! important; }

#detailview_15 { background: #7E7FA0 ! important;  border: 0px ! important; }

.UnpublishedPrimaryBorder { border-color: #7E7FA0 ! important; }

.Section {
  margin-left: 10px ! important;
  margin-bottom: 0px ! important;
  border-width: 0px ! important;
  padding: 10px ! important;
}

.RecentNotebooksHeader { display: none ! important; }

}
