css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kongregate.com") {
    body.cinematic_mode {
        position: fixed;
    }
    
    .cinematic_mode .cinematic_overlay {
        opacity: 1;
    }
    
    .cinematic_mode #game {
        position: fixed !important;
        top: 0;
    }
    
    #cinematic_close_link {
        position: fixed;
        right: 5px;
        top: 5px;
        opacity: 0.6;
    }
    
    #cinematic_bug_link {
        position: fixed;
        right: 5px;
        bottom: 5px;
    }
}
