@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.heavens-above.com") {

  body, select, input, .menu, .menu2, .menu3, .pagehead, .userTable {
    background-color: black;
    color: red;
    font-weight: bold;
  }
  
  table, hr, td, select, input {
    border-color: darkred !important;
  }
  
  a {
    color: #FF2525 !important;
    font-weight: bold;
  }
  
  #ctl00_hyperlinkLogo img {
    display: none;
  }
  
  .darkrow, .tablehead, tr {
    background-color: black !important;
    color: #FF4040 !important;
  }
  
  .lightrow {
    background-color: #050505 !important;
    color: red !important;
  }

}
