css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("getprismatic.com") {
html, body, body * {  
  font-family: Ubuntu, DejaVu Sans !important;
  background-color: #3c3c3c !important;
  color: #dcdccc !important;
}
  
#container.has-nav {
  padding-left: 55px !important;
}  

.doc-action-user-bookmarks {
  border: none !important;
}

.dropdown-overlay {
  background-color: rgba(60, 60, 60, 0.6) !important;
}  

.doc-title, .doc-interest-link * {
  color: #6C6C9C !important;
}
[data-icon], .icon  
{
  font-family: Prismaticons2-Regular !important;
}
}
