css:
@-moz-document domain("www.tvguide.com") {

/*remove wasted empty space between listings, allowing you to see more listings without scrolling*/
.listings-channel-row, .listings-ad-row, .listings-outstream-row, .listings-channel, .listings-channel, .listings-program, .listings-channel-franchise {
  height:inherit !important; line-height:inherit !important;
  } 

/*ditto*/
.listings-details-content {
  padding-top:0 !important; padding-bottom:0 !important; 
  min-height:1px !important;
  }

/* removes unhelpful image from expanded view of listing; this also reduces the time required to open the listing*/
.listings-details-image {display:none !important;}

/*reduces empty space around listing details in expanded state*/
.listings-details-inner {padding:10px !important;}

/* Reduces font size of episode description in expanded listing */

.listings-details-body {
    font-size:13px !important;
  }

}
