css:
@-moz-document domain("pelis24.com") {
div#sidebar {
    display: none;
}

.vsep {
    padding: 0 !important;
    border-radius: 9px;
    background: #040404;
}

.wsh {
    background: none;
}

.ui-widget-header {
    background: none !important;
}

.maincont>div:nth-child(2) {
    clear: both;
  	margin: 0 !important;
}

div#tabs {
    background: black;
    border: 0;
    margin: 300px 0;
    text-align: center;
}


.ui-tabs .ui-tabs-nav {
    text-align: center;
    border: 0;
    display: inline-block;
}

body {
    background: black;
}
#midside {
background: black;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background: transparent !important;
    border: 0 !important;
}

.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited {
    color: #424242 !important;
}
.ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {
    color: #999 !important;
}
img[src="/images/peliculapantallacompleta.jpg"] {
	display:none;
}
div#tabs-7 {
display: none;
}
.wwide {
    min-width: 0;
}

.wrapper {
    min-width: 0;
    width: 100%;
    max-width: none;
}

.container {
    margin: 0;
}

#midside>div:nth-child(2) {
    margin: 0 !important;
}

.dpad {
    margin: 0;
}
.maincont>div:nth-of-type(2) {
    margin: 0 !important;
}

.ui-tabs .ui-tabs-panel {
    padding: 0 !important;
}

.headlinks>div:nth-child(1) {
    right: 134px;
}
}
