css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slatestarcodex.com") {
  body {
    padding: 0;
  }
  #pjgm-wrap {
    width: auto;
    margin: 0;
  }
  #pjgm-menubar .menu-header, div.menu, #pjgm-bigtitle, #pjgm-main, #pjgm-menubar {
    width: 100%;
    margin: 0;
    box-sizing: border-box;
  }
  #pjgm-header, #pjgm-ender {
    width: auto;
    background-image: none;
    background-color: #648ad4;
    padding: 0px;
    margin: 0px;
  }
  #pjgm-main {
    background-image: none;
    background-color: white;
    padding: 0px;
    margin: 0px;
  }
  #pjgm-bigtitle {
  }
  #pjgm-title, #pjgm-description {
    width: auto;
  }
  #pjgm-title {
    font-size: 48px;
    line-height: initial;
    padding: 4px;
  }
  #pjgm-description {
    font-size: 14px;
    color: #e4e4e4;
    padding: 4px;
  }
  a.pjgm-home, #pjgm-menubar ul > li, a.pjgm-feed {
    background-image: none;
    border-style: solid;
    border-color: #CCC;
    font-size: 14px;
  }
  a.pjgm-home, #pjgm-menubar ul > li {
    border-right-width: 1px;
  }
  a.pjgm-feed {
    border-left-width: 1px;
  }
  #pjgm-menubar > span.pjgm-ender {
    display: none;
  }
  .pjgm-postmeta {
    text-transform: none;
  }
  .widget-container {
    border-color: #D5D5D5;
  }
  #primary {
    background-color: #e4e4e4;
  }
  #pjgm-main .widget-area ul {
    text-shadow: none;
  }

}
