css:
/* i really want this to be global */

/* global. */ * { background-color:#000 !important; color:#FFF !important; border-color:#FFF !important; }
/* global. */ :link,:visited,*:hover,:active { background-color:#000 !important; color:#0BF !important; }
/* global. */ input { background-image: initial !important; }
/* firefox */ *::-moz-selection { background-color:#000 !important; color:#FFF !important; border-color:#FFF !important; }

img, .img, [class*="Img"] {opacity: 0.03 !important;}
img:hover, div:hover > img, a:hover > img, .img:hover, [class*="Img"]:hover {opacity: 1 !important;}
.stageWrapper * {background-color: transparent !important;}
:not([class*="Img"]):not(.img) {background-image: none !important;}

/* patch: Ff_UI_tabcorners */ [id="tab-background-fill"] { background-color: initial !important; }
/* patch: userstyles(x) */ section#style-info div#main-style-info div[id] { background: initial !important; }
/* patch: google(3) */ body#gsr div:not(#hplogo),body#gsr a,body#gsr button { background: initial !important; }
/* patch: ustream(1) */ div#HeaderGrad { background-image: initial !important; }
/* patch: hitbox(3) */ div.mediaControls,div[ng-click="doubleClickFullscreen()"],button[class] { background-color: initial !important; }
