css:
@-moz-document domain("questionablequesting.com") {
/* Allows you to see what you're typing in search and input forms */
#QuickSearch {
	top:-33px
}

/* Changes the search box colors */
#QuickSearchQuery,.textCtrl {
	color:#000!important;
	background:#FFF!important
}

/* Editor customization */
.mce-btn button,.mce-menu,.mce-panel {
	background-color:lightgray
}

.mce-btn button {
	padding:3px 6px
}

.mce-panel {
	background-image:none
}

.mce-menu-item:hover,.mce-menu-item.mce-selected,.mce-menu-item:focus {
	background-image:-moz-linear-gradient(top,rgba(176,176,176,1),rgba(176,176,176,1));
	background-image:-webkit-linear-gradient(top,rgba(175,175,176,1),rgba(176,176,176,1));
	background-image:-o-linear-gradient(top,rgba(69,137,210,1),rgba(69,137,210,1));
	background-image:linear-gradient(to bottom,rgba(176,176,176,1),rgba(175,175,175,1))
}

.mce-btn.mce-disabled button,.mce-btn.mce-disabled:hover button {
	opacity:.8
}

.mce-container *,.mce-widget,.mce-menubtn span,.mce-primary button,.mce-primary button i {
	color:#1e1e1e
}

.mce-content-body {
	background-color:#1e1e1e
}

.mce-tabs,.mce-tab {
	border-color:#030303
}

.mce-tab,.mce-primary button,.mce-primary button i {
	text-shadow:none;
	background-color:#9d9d9d
}

.mce-tab.mce-active {
	background:#d3d3d3
}
}
