css:
@-moz-document url-prefix("http://www.wonderhowto.com/") {
body {background:#222}

#page-header { position:fixed !important; background:#222 !important; }

#frontPageCallToAction {background: #000 !important; }

#frontPageCallToAction .main-headline { color:#dae !important }

div#aph_10518929_tmnp_wonderhowto_homepage_top_728x90 { min-height: 0px !important; height: 0px !important; }

#sidebar .ad-wrapper { display:none !important; }

#main-wrapper { background: transparent !important; -webkit-box-shadow: none !important; box-shadow: none !important; border:none !important}

#abgc img { display: none !important; }

.frontpage.item .description { background-color: #111 !important; }

a { color:#dae }

#sidebar .box { background: #111 !important; }

.txt-clr-dark, .ui-widget-content .txt-clr-dark { color:#dae !important; }

#world-activities-wrapper .activity-comment { background: #000 !important; color: #aaa !important; }

.related-sidebar-container h4 a { color:#dae !important }

.related-sidebar-container h4 a:hover { color:#29d !important }

#adunit { display:none !important; background:#222 !important; }

#ads { display:none !important; background:#222 !important; }

#abgc img { display:none !important; background:#222 !important; }

#google_ads_frame1 { display:none !important; background:#222 !important; }

/*---------------------------Chrome-Scroll----------------------*/

::-webkit-scrollbar {
  width: 9px;
  height: 9px;
}
::-webkit-scrollbar-track-piece {
  background-color: #222;
}
body::-webkit-scrollbar-track-piece {
  background-color: #222;
}
::-webkit-scrollbar-track-piece:no-button {}
::-webkit-scrollbar-thumb {
  background-color: #444;
  border-radius: 3px;
}
::-webkit-scrollbar-thumb:hover {
  background-color: #777;
}
::-webkit-scrollbar-thumb:active {
  background-color: #666;
}
}
