css:
@-moz-document domain("deviantart.com") {
/* stickys top bar */

#overhead-collect
{
position: fixed;
}


/* remove ads */

.ad-blocking-makes-fella-confused,
.modframe-671589157,
.dp-ad-chrome,
.partial-ad,
.sleekadbubble,
#gmi-MessageCenterDockAd,
.mczone-you-know-what,
.gr-adcast,
.guideblock,
#fake-col-left,
div.f.gruze-sidebar.left-sidebar.module-min.module-blocks.match-body-height,
#badges-page>.rr>:first-child,
.upsell-button,
.disabled_subscription,
div.mczone div.mczone-empty,
.mc-ad-chrome
{
display: none;
}

#sidebar-you-know-what
{
width: 0;
}



/* corrects spacing problem caused by ad removal */

.f,
#gruze-main
{
padding: 1px;
margin: 0!important;
margin-left: 0!important;
}

/* hide stuff normally unused */
#oh-menu-shop .mi:not(:first-child),
#oh-menu-shop .oh-darker,
#oh-menu-mobile, #oh-menu-upgrade, .browse-top-bar, .browse-facet-search
{
  display: none;
}
}
