css:
@-moz-document domain("duckduckgo.com") {
body, html
{
  background-color: #000 !important;
}

*
{
  border-color: #333 !important;
}

.search__autocomplete *
{
  background-color: #222;
  color: #eee;
}
.search__autocomplete .acp strong
{
  color: #aaa;
}

.detail__wrap
{
  background-color: #222;
}
.detail__title a
{
  color: #aaa !important;
}

#header_wrapper,
#header_wrapper::before,
#header_wrapper::after,
#header,
#header::before,
#header::after,
.zci,
.zci::before,
.zci::after
{
  background-color: #000 !important;
}

.zci__metabar-wrap
{
  background-color: #222 !important;
  color: #eee !important;
}
.zcm__link--sub
{
  color: #eee !important;
  font-size: 1.2em !important;
}
.zcm__link--sub.is-highlighted,
.zcm__link--sub:hover
{
  color: #aaa !important;
}
.tile-wrap
{
  background-color: #000;
}
.tile-wrap .tile
{
  background-color: #222;
  color: #eee;
}
.tile-wrap .tile__title
{
  color: #eee;
}
.tile-wrap .tile__body a
{
  color: #eee !important;
}
.tile-wrap .tile__foot
{
  background: none;
}

.result,
.result .result__a,
.result .result__snippet
{
  color: #eee !important;
}
.result .result__snippet b
{
  color: #fff;
}

.result
{
  background-color: #222 !important;
}
.result.highlight
{
  background-color: #444 !important;
}
}
