css:
@-moz-document domain('storyform.co') {

p, h3,
.control-figure .image-overlay.top-left figcaption,
.TextLeftSmall-Container .Text span.pullquote, 
  .TextLeftSmall-Container .Text figure ,
.MediaRight-Container .MultiColumnRegion {
direction: rtl;
text-align: right;
}

.control-multicolumnregion > * {
padding-right: 40px;
}
.control-multicolumnregion>:first-of-type {
padding-right: 0;
}

.resource-flow blockquote, 
 .control-region blockquote, 
 .control-text blockquote {
border-right: #111 solid 3px;
padding-right: 24px;
direction: rtl;
}

.first-region .first-paragraph:first-of-type:first-letter, 
.control-text .first-paragraph:first-of-type:first-letter, 
.resource-flow .first-paragraph:first-of-type:first-letter, 
.drop-cap {
font-size: 78px;
margin: 8px 2px 0 18px;
line-height: 64px;
float: right;
}

}
