css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("translate.google.ru"), domain("translate.google.com") {
	#gt-text-c, #gt-apb-main {
		float: none;
		margin: 0 auto;
	}

	#result_box {
		min-height: 300px;
	}

	#contribute-target, #source, .gt-hl-layer {
		min-height: 315px;
		padding: 0 8px 0;
	}

	#gt-src-wrap > div {
		padding-top: 8px;
	}

	#gt-src-wrap > div {
		margin-top: 0px; 
	}

	#gt-src-wrap #gt-src-tools {
		padding-top: 4px;
		padding-bottom: 1px;
	}

	#gt-text-c {
		padding-top:0
	}

	#gt-src-tools-l > #gt-input-tool {
		display: none;
	}

	#gt-input-tool {
		float: left;
	}

	#gt-text-top {
		padding-bottom: 10px;
	}
}
