css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stumbleupon.com") {

body {
	font-family: "lucida grande",verdana,tahoma,arial,sans-serif !important;
	font-size: 12px !important;
	color: black !important;
	background: url('http://i292.photobucket.com/albums/mm16/onyxstone_at_su/SU_BGs/002.jpg') !important;
}

a {
color:#206BA4 !important;
}

a:hover {
color:#3B5998 !important;

}

a:visited {
color: #3B5998 !important;
background-color: transparent !important;
}

a:visited:hover {
color: #3B5998 !important;

}

h1 {
color: #4D4D4D !important;
}

h2 {
color: #4D4D4D !important;
}

h3 {
color: #4D4D4D !important;
}

h4 {
color: #4D4D4D !important;
}

span, span.textSuccess {
color: #4D4D4D !important;
}

div.sbHighlight2, li.btnAlert, span.btnAlert {
background: transparent url('http://i292.photobucket.com/albums/mm16/onyxstone_at_su/SU_BGs/new-8.png') bottom left !important;
}

li.current {
background: transparent !important;
}

li.current > a{
background: transparent !important;
}

.userBcard > *, .userBcardBg, div.bubbleHead, div.bubbleContent, div.groupBanner, div.bubbleBottom, #listTopics{
background: white !important;
}


.headerWrapper {
	background: 
	transparent
	url('http://i292.photobucket.com/albums/mm16/onyxstone_at_su/Patterns/topgradient-3-1-1-1-1-1-1-1.png')
	repeat-x top left !important;
}


#nav > ul > li,  #nav > ul > li > a{
	background: gray !important;
	padding: 5px 5px 5px 5px !important;
	color: white !important;
	text-transform: lowercase !important;
	font-variant: small-caps !important;
}

#nav > ul > li > a:hover {
	background: white !important;
	color: gray !important;
}

#footer {
	background: url('http://i292.photobucket.com/albums/mm16/onyxstone_at_su/Patterns/topgradient-3-1-1-1-1-1-1-1.png')
repeat-x top left !important;
margin-top: 0px !important;
}

#footer a, #footer a:visited {
	color: white !important;
}

div.contentWrapper {


}



#wrapper {
	width: 85% !important;

	
}





div.sidebar, td.sidebar {
	background: white !important;
}


td.main {
       background: white !important;
}

td.sidebar {
	border-left: 1px dashed gray !important;
}

div.inlineTabWrapperPublic, div.inlineTabWrapper,
div.sectionCmds, 
#blogEntries > dl > dt, #cloudTopics,
div.listPeopleSm 
{
	background: transparent url('http://i292.photobucket.com/albums/mm16/onyxstone_at_su/SU_BGs/new-8.png') bottom left !important;
}

#blogEntries > dl > dt {
padding: 5px auto 5px auto !important;
}


.dividerBottom {
	border-bottom: none !important;
}
}
