@-moz-document domain("indeed.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("indeed.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

selector {
	text-transform: capitalize!important;
}

#vjs-cmL,
.ns-card-header,
.ns-step,
:focus
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.ns-desktop-carousel-container {
    overflow: visible!important;
}

table {
    border-spacing: 12px;
}

body.janus #what_acdiv.acd ul,
body.janus #acdiv.acd ul,
body.janus #acdiv_where_alert.acd ul
{
    border: 2px solid #c50000 !important;
}

/*___ Fonts ________________________________________________________________________*/

.icl-Card,
.icl-GlobalFooter-copyright,
.icl-TextPromo--isExpanded .icl-TextPromo-text,
.icl-TextPromo-text,
.icl-WhatWhere .icl-TextInput-helpText--whatWhere,
.icl-WhatWhere .icl-TextInput-label--whatWhere,
.icl-u-textSize4,
.icl-u-textSize5,
.ns-card-header-text,
.ns-root,
a, h1, h2, h3, h4, h5, h6, ol, p, ul,
body,
body.janus *,
body.janus .jasxcustomfonttst-useAvenir *
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

h1 font, h1,
.ns-card-header-text {
	font-size: 21px!important;
}

#resultsBody #pageContent #resultsCol .row .salarySnippet .salary,
#resultsBody #pageContent #resultsCol .row .serp-ResponsiveEmployer .serp-ResponsiveEmployer-heading,
#resultsBody #pageContent #resultsCol .row .serp-ResumeMatch .serp-ResumeMatch-heading,
#resultsBody #pageContent #resultsCol .row .snip .summary,
#resultsBody #pageContent #resultsCol .row.unifiedRow .summary,
.icl-Card-headline,
body.janus .rbHeader .ws_bold
{
	font-size: 19px!important;
}

.ns-root,
body.janus #auxCol .ja_rec_confirm_ui_tst,
body.janus #femp_list .femp_header,
body.janus #jobalerts .jobalertlabel,
body.janus #tjobalerts .jobalertlabel,
body.janus #univsrch-salary-v3 #univsrch-salary-title,
body.janus .rbList li a .rbLabel,
td
{
	font-size: 17px!important;
}

selector {
	font-size: 15px!important;
	line-height: 27px!important;
}

selector {
	font-size: 14px!important;
	line-height: 19px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.jobsearch-RecentSearches-grid {
    left: 0;
}

.jobsearch-RecentSearches-grid {
    margin-left: 0!important;
}

/*___ Margins ______________________________________________________________________*/

.jobsearch-RecentSearches-grid,
.rbSection {
	margin: 12px!important;
}

selector {
	margin: 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

#popover-link-x,
.rbSection {
	padding: 12px!important;
}


.icl-Grid--gutters>.icl-Grid-col {
    padding: 5px 1rem;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

table#pageContent,
.icl-Container {
	max-width: none!important;
}

selector {
	width: auto!important;
}

#resultsBody {
	width: 98%!important;
}

#resultsCol {
    width: 65%!important;
}

#refineresultscol, #branding-td {
    width: 16%;
}

.jobsearch-RecentSearches-grid {
    width: 97vw!important;
}





}

@-moz-document domain("indeed.com") {
/*__________________________________________________________________________________*/

body.janus div#g_nav a,
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

body.janus #resultsCol .pagination b,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.icl-Card-body,
.icl-Card-cta,
.icl-TextPromo-text,
.icl-u-textColor--secondary,
.ns-root,
body,
body.janus #refineresults span[role="heading"]+div .no-wrap b,
body.janus #resultsCol #bjobalerts,
body.janus #resultsCol #primePromo,
body.janus .rbList li a .rbCount,
body.janus .rbOpen .more_link span,
body.janus .result .JobTags-list,
body.janus .result .company,
body.janus .result .snip,
body.janus .result .summary,
body.janus .result .turnstileLink .slNoUnderline,
body.janus [data-tn-element=reviewStars] .slNoUnderline,
body.janus [data-tn-element=reviewStars]:link,
li,
p
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
