/*___ Khaki ________________________________________________________________________*/

#vjs-jobtitle,
body.janus .result .company a,
.basic-profile-headerText,
.icl-Card-headline,
.ns-card-header-text,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.serp-ResponsiveEmployer-heading,
body.janus #jobsearch #what_label_top,
body.janus #jobsearch #where_label_top,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

body.janus #resultsCol .pagination,
#resultsCol .result-link-source,
body.janus #refineresults span[role="heading"]+div,
h5,
h6,
label
{
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

#resultsCol .date,
.icl-GlobalFooter-copyright,
time
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

body.janus .rbList li a .rbLabel,
.row .snip .salary,
.row .sjcl .salarySnippet span,
.row.unifiedRow .salarySnippet .salary
{
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/


selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#recentSearches .icl-NavigationList-items,
body.janus #alertemail,
body.janus #talertemail,
body.janus #balertemail,
body.janus #jobsearch .inwrap #what.input_text,
body.janus #jobsearch .inwrap #where.input_text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Misc _________________________________________________________________________*/


.location {
    color: #af0!important;
}
























}

@-moz-document domain("indeed.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: #000 url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray ______________________________________________________________*/

