css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("senseis.xmp.net") {
  .mainpage, .subpage, .discussionpage, .messagepage, body, div, table, td {
     background-image:none;
     background-color: #000 !important;
     color:#AAF !important;
   }
  
   a:link { color:#FAA; }
   a:visited { color:#B88; }
  
  input {
    border-radius:4px;
    background-color:#111;
    color:#FFA;
  }
  img:not(.stone) {
    background-color:#FFF;
  }
  th {
    background-color:#222 !important;
  }

  ul.tools {
    background-color:#000 !important;
  }
  
  ul.tools a:hover {
	  background-color: #221; 
  }
  ul.editmenu a:hover {
    background-color: #000;
  }

}
