css:
@-moz-document url-prefix("https://www.allmystery.de/pn/"), url-prefix("http://www.allmystery.de/pn/") {
a {
    color: #1189E0 !important;
    background-color: rgba(0,0,0,0.7) !important;
    border-radius: 3px 3px !important;
    box-shadow: 0px 0px 30px #000000 !important;
}

body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/bc8041_Auswahl_005.png")!important;
    background-size: 100% 100% !important;
    background-attachment:fixed !important;
}

.titelg {
    border-radius: 3px 3px !important;
}

.message, .message_info {
    background-color: rgba(0,0,0,0.8) !important;
    border-radius: 3px 3px !important;
    border-color: #1BB1FA !important;
}

div.message.pnMessage {
    border: 1px solid #1BB1FA !important;
}

.userpic {
    border-radius: 3px 3px !important;
    border-color: #1BB1FA !important;
}

textarea {
    border-radius: 3px 3px !important;
}
}
