css:
@-moz-document url-prefix("https://www.allmystery.de/pn/"),
url-prefix("http://www.allmystery.de/pn/") {
a {
    color: #CE50B6 !important;
    background-color: rgba(0,0,0,0.7) !important;
    border-radius: 3px 3px !important;
    box-shadow: 0px 0px 30px #000000 !important;
}

body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/df7ecd_kitty_bg.jpg")!important;
    background-size: 100% 100% !important;
    background-attachment:fixed !important;
    color:  #CE50B6 !important;
}

.titelg {
    border-radius: 3px 3px !important;
    border-bottom: 1px solid transparent !important;
}

.message, .message_info {
    background-color: rgba(77,77,77,0.8) !important;
    border-radius: 3px 3px !important;
    border-color: transparent !important;
}

.userpic {
    border-radius: 3px 3px !important;
    border-color:  #CE50B6 !important;
}

textarea {
    border-radius: 3px 3px !important;
    border-color:  #CE50B6 !important;
}
}
