css:
@-moz-document domain('www.nederlanders.fr'), domain('nederlanders.fr') {

  div.xg_ad {display:none !important;}
  #aswift_1_expand{display:none !important;}
  #xg_head{margin-bottom:20px;}
  h3 > a{font-weight:normal;}
  a, a:hover, #xg_navigation ul li a, ul.tabs a, #xg_navigation ul div.xg_subtab ul li a{color:#0061B7;}
  a:visited{color:#004582;}
  ul.tabs li a:hover{color:#fff; text-decoration:none;}
  
  body, .xg_module_head{color:#111;}
  body, h1, h2, h3, h4, h5, h6, p{font-family:verdana;}
  h1{font-size:1.6em;}
  h2{font-size:1.4em;}
  h3{font-size:1.2em;}
  h4{font-size:1em;}
  
  .xg_blog h3.title{font-size:1.3em;}
  
  iframe[id^=google]{display:none !important;}
  
}
