css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.nicovideo.jp/video_top) {
#column main .data-main-contents, #g_politics
  { display: none !important; }
}

@-moz-document url-prefix(http://www.nicovideo.jp/video_top) {
nav.mainMenu div.inner ul.navigation li:nth-child(4)
  { display: none !important; }
}

@-moz-document url-prefix(http://www.nicovideo.jp/ranking/fav/daily/all) {
.header .menuBar .mainMenu .inner .navigation li:nth-of-type(4)
  { display: none !important; }
}

@-moz-document url-prefix(http://www.nicovideo.jp/video_top) {
nav.mainMenu div.inner ul.navigation li:nth-child(4)
  { display: none !important; }
}

@-moz-document url-prefix(http://www.nicovideo.jp/ranking) {
  table.matrix { width: 880px !important; }
  table.matrix thead tr th:nth-child(4) { display: none !important; }
  table.matrix tbody tr.item_row td:nth-child(4) { display: none !important; }
  table.matrix tfoot tr th:nth-child(4) { display: none !important; }
  table.matrix tfoot tr.all_category td:nth-child(4) { display: none !important; }
}
