css:
@-moz-document domain("adf.ly") {
#Interstitual table tbody tr:not([height*="1%"]),
#sitebar #bottomAd,
#sitebar #bottom,
div[style*="display:inline-block; text-align: center; padding-top: 20px; height: 20px; width:60%; color: #FFFFFF; letter-spacing:2px"],
#top span:first-child,
#skip_ad_button
{
  display: none!important;
}

#Interstitual #top
{
  background-image: none;
  position: relative!important;
}

#skip_button
{
  background-image:url("http://static.adf.ly/static/image/skip_ad/en_tran.png");
  width: 155px!important;
  height: 41px!important;
  right: auto!important;
}

#top span
{
  position: absolute!important;
  left: 50%;
  margin-left: -77.5px;
  width: 155px!important;
}
  
#Interstitual table tbody tr td
{
  position: absolute;
  top: 30%;
}

#please_wait
{
  margin-left: -155px;
}

#Interstitual
{
  background: rgb(8,46,73)!important;
}
  
}
