       background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,black), color-stop(100%,#000482)); /* Chrome,Safari4+ */
       background: -webkit-linear-gradient(top,  black 0%,#000482 100%); /* Chrome10+,Safari5.1+ */
       background: -o-linear-gradient(top,  black 0%,#000482 100%); /* Opera 11.10+ */
