@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mathprofi.ru") {
  h1, h2, h3, h4, h5, h6, font[color=red] {
    color:#07f !important;;
  }
  table[border], table[border] * {
    border:0 none !important;
  }
  /* реклама */
  table[width="610"][height="200"] {
    display:none;
  }
  
  p.classbar:nth-child(2),
p.classb,
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > noindex:nth-child(11) > p:nth-child(2),
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > noindex:nth-child(11) > p:nth-child(3)
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > noindex:nth-child(13) *
{
  display:none;
}

}
