css:
@-moz-document domain("worldcosplay.net") {
* {font-family: Segoe UI, Leelawadee UI, Meiryo UI !important}
.fa {font-family: FontAwesome !important}

.avatar,.avatar img, .avatar .user, .edit_btn, .card.follows li, .card.follows li a.more, .member-list li .icon, .nologinMenu, .profilePhoto, .rank-list ul .icon, .shortcut .key, .user img, .user_icon img, .userIcon>img {
	border: none !important;
	border-radius: 0px !important;
    -webkit-border-radius: 0px !important;
	-moz-border-radius: 0px !important;
	-ms-border-radius: 0px !important;
	-o-border-radius: 0px !important;
}
  .new-marker::after {color: red !important; background-color: transparent !important; border-radius: none !important; text-transform: uppercase !important; font-size: 12px !important}

.card.follows li a.more {width: 60px !important;height: 60px !important}

.globalName, .secondary_name, .stat {font-weight: lighter !important}
.photo_title {font-weight: normal !important}
}
