css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anandtech.com") {
  
  body{
    font-family: "Quercus";
  }
 
  div.generic_cont p, div.articleContent p {
    margin-top: 13px;
    margin-bottom: 13px;
    color: #444;
    line-height: 24px;
  }
  
  div.articleContent {
    font-family: 'Quercus';
    font-size: 18px;
  }
  
  div.articleContent table tr td.contentwhite {
    font-weight: bold;
  }
  
  div.articleContent table tr.tlblue td {
    font-weight: bold;
  }
  
  td {
    vertical-align: middle;
    font-family: Ubuntu;
    font-size: 13px;    
  }
  
  #ulComments {
    font-family: 'Open Sans';
    font-size: 14px;    
  }
  
  #ulComments span {
   line-height: 24px !important;  
  } 
 
}
