css:
@-moz-document url-prefix("http://giveallamagetallama.deviantart.com/") {
.catbar,
#gmi-GZone:not(:last-child),
#gmi-GZone:only-child,
#fake-col-right,
.cc-meta>*:not(.cc-name),
.cc-signature,
.previewbox .cc-meta,
.user-symbol,
.avatar,
.reply,
i[class],
.gr-top,
.drawPlz,
.show-image-thumbs,
.drawplz-attachment-ctrl,
#fake-col-left,
.left-sidebar,
#depths,
.username,
#gmi-CCommentThread>#gmi-CComment .text,
.nest,
.ccname,
.scripted>span,
#cooler-comment-submit:not(:first-child),
.previewclose,
.uparrow,
.creason,
.gotobox>.text,
.gotobox>a,

.oclb-success,
.oclb-already
{
  display: none!important;
}

.previewbox>.previewbox-content,
.previewbox-content .text,
.oh-l>span>span
{
  display: block!important;
}

.previewbox-content .text *:not(.cc-signature)
{
  display: initial!important;
}

.cc-avatar,
#gmi-TalkPostWrapper,
#gruze-main
{
  margin: 0;
}

#cooler-comment-submit
{
  position: fixed;
  width: 18%;
}

#gmi-CCommentThread,
.pagination-wrapper 
{
  position: relative;
  width: 80%;
  height: 100%;
  left: 20%;
}

#gmi-CCommentMaster
{
  position: relative!important;
}

#group,
.cc-in
{
  min-height: 0!important;
}

.cc-in,
.ccomment-form
{
  padding: 0!important;
}

.smbutton,
.oclb-give,
.oclb-error,
.oclb-giving
{
  width: 100%!important;
  margin: 5px 0 5px 0!important;
  display: block!important;
}

.cc-without-moods
{
  margin-bottom: 10px;
}

.cc-meta
{
  margin: 0;
}

.gotobox
{
  padding: 5px 4px 5px 5px!important;
  width: 50px;
}

.gotobox input
{
  width: 40px!important;
  border-right-width: 1px!important;
  border-radius: 3px!important;
}


}
