css:
@-moz-document url("https://www.focusatwill.com/music/#player"){
   body{
   background-color: #222;
   }
   
   #scroller{
   background: #333;
   border: 3px solid #111;
   }

   #home-header{
   background: #444;
   border: 1px solid #111;
   }

   .trackDetail{
   text-align: left;
   }

   #login-status-container{
   visibility: hidden;
   }
  
   #home-body, #background-overlay{
   background: none !important;
   }

   .logo{
   -webkit-filter: invert(100%);  
   }
}
