@-moz-document url-prefix("https://mail.google.com") {
.yW {
  text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
    visibility: hidden;
	word-spacing:-999px;
	letter-spacing: -999px; 
}

.yP {
  text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
    visibility: hidden;
	word-spacing:-999px;
	letter-spacing: -999px; 
}


.yP:after {
		content:  attr(email) ;
		color: #000;
		font-weight:100;
		text-indent: 0;  		
  		line-height: initial; /* New content takes up original line height */
    	visibility: visible;
		word-spacing:normal;
		letter-spacing:normal; 

    	white-space:nowrap;
    	display:inline-block;
  		float: left;
}
  
.zF {
  text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
    visibility: hidden;
	word-spacing:-999px;
	letter-spacing: -999px; 
}


.zF:after {
		content:  attr(email) ;
		color: #000;
		font-weight:100;
		text-indent: 0;  		
  		line-height: initial; /* New content takes up original line height */
    	visibility: visible;
		word-spacing:normal;
		letter-spacing:normal; 

    	white-space:nowrap;
    	text-overflow:ellipsis;
    	width:100px;
    	display:inline-block;
  		float: left; 
}
