css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(99gamers.com) {
.for_sale_block .photo, .for_sale_block .photo img {height:90px!important;width:180px!important}
.for_sale_block masonry_item title_block {background:none;border:none;}
.for_sale_block .photo {border: 1px solid #000; box-shadow: 0px 2px 10px #aaaaaa;}
.for_sale_block .photo:hover {box-shadow: 0px 0px 1px #aaa!important;border:1px solid #fff;}
.offer_desc {display:none}
.prof_game_title div:first-child {float:left!important;display:block!important;position:absolute;top:-18px;left:-6px;width:182px;height:37px;background-image: url(data:image/png;base64,);opacity: 1!important;letter-spacing:-1px;line-height:12px;word-spacing:-1px;}
.prof_game_title {background:none!important;display:block!important;top:64px;height:16px!important;text-shadow:0 -1px 1px #fff;}
.prof_game_title span {background:none!important;}
.for_sale_block.masonry_item.sell_block.masonry-brick {height:170px!important;}
.prof_game_title .price,.inner_monetary_game{display:none!important;}
}
