css:
@-moz-document url("https://www.linkedin.com/"), url-prefix("https://www.linkedin.com/") {
.nav-v5-2-header #top-header {
	background-color : #AD1B1B !important;
  	background-image : none !important;
  
}

.nav-v5-2-header .utilities .activity-toggle.inbox-alert:before {
	color: #FFF !important;
            
}

.nav-v5-2-header .utilities .activity-toggle.notifications-alert:before{
    color: #FFF !important;      
	
}
          
.nav-v5-2-header .utilities .activity-toggle.add-connections-btn:before {
	color: #FFF !important; 
}
                    
.global-search .search-scope .label {
	background: transparent !important;
	background-image: none !important;                            
}
                          
                          
.global-search .search-scope .label span.styled-dropdown-select-all:before {
	color : #FFF !important;                                    
}
                                  
img.photo, span.photo {
	border : none !important;
}

                                  
.nus-feed-container .feed-item .feed-photo.photo, .feed-redesign #my-feed-post.katify .feed-item .feed-photo.photo {
	padding-left : 20px !important;
}

#pagekey-member-home, #pagekey-uas-consumer-login-internal {
	margin-top : 110px;
}

ul.nav.premium-nav{
	display : none !important;
}

.profile-card .profile-picture img {
	padding-left : 12px;
}
}
