css:
@-moz-document regexp('https:\/\/codetree.com\/projects\/.*\/issues.*') {
  
  .issue-number:after {
    position: absolute;
    left: 70%;
    background-color: #fff;
    box-shadow: -8px 0 8px #fff;
}  
  
  .unsorted .issue-number:after {
    background-color: #fdfaf8;
    box-shadow: -8px 0 8px #fdfaf8;
  }

  .issue-number[title]:after {
    content: attr(title);
  }
  .issue-number[data-original-title]:after {
    content: attr(data-original-title);
  }

}
