css:
@-moz-document domain("www.w3schools.com") {
.col-lg-2.col-md-2.col-sm-12,
#mainLeaderboard,
div[style="xborder:1px solid;overflow:hidden;"],
.col-lg-10.col-md-10.col-sm-9.leaderboard>.row:last-child
{
  display:none;
}

.col-lg-10.col-md-10.col-sm-12.main
{
  width: 100%;
}

.chapter,
.prev,
.home,
.next
{
  position: fixed;
  bottom: 0;
  right: 0;
  width: 83.333333333%;
  z-index: 1;
  padding: 10px 1% 10px;
  font-size: 14px;
  text-transform: uppercase;
}

.next .chapter,
.prev .chapter
{
  color: white!important;
}

.prev .chapter
{
  background-color: #5F5F5F!important;
}

.container-fluid.master
.col-lg-2.col-md-2.col-sm-3.menu
{
  max-height: 100%;
} 
}
