css:
@-moz-document url-prefix("http://www.webgame.cz/") {
  img.wginfo:nth-child(5) , #footer , #header > form:nth-child(4) > p:nth-child(3) > a:nth-child(3) {
    display: none !important;
  }
  #header form .info {
    top: 3.7em !important;
  }
  #content #wgnoviny {
    margin-left: 10px !important;
  }
  #left_menu>ul:nth-child(1)>li:nth-child(1) {
    display: none !important;
    }
  #left_menu>ul:nth-child(1)>li:nth-child(2) {
    display: none !important;
  }
  #left_menu>ul:nth-child(1)>li:nth-child(9) img {
    display: none !important;
  }
  #left_menu>ul:nth-child(1)>li:nth-child(9)>br {
    display: none !important;
  }
  div.c[style*="text-align:center"] {
    display: none !important;
  }
  div#icontent h2 {
    display: none !important;
  }
  div#icontent td[class="valk"] {
    width: 250px !important;
  }
  div#icontent strong[class="valk"] {
    color: #61A54A;
  }
  div#content>br {
    display: none !important;
  }
  div#icontent h1 {
    display: none !important;
  }
  div#icontent>p {
    font-size: 95% !important;
  }
  #icontent > a[href*="s=getplus"] {
    display: none !important;
  }
  p.goodtext:nth-child(2) {
    content: 'Test string';
  }
  @-moz-document regexp(".*p=pracovna.*") {
    table.vis_tbl:nth-child(4) {
      display: none !important;     
  }
  }
  p.goodtext:nth-child(2):after {
    color: red !important;
    content: ' !!!';
  }
  .menu span[style*="color: #00FFFF; font-weight: bold;"] {
    display: none !important;
  }
  @-moz-document regexp(".*s=najittag.*") {
    h1 {
      display: none !important;
    }
  }
  @-moz-document regexp(".*s=aliazebr.*") {
    #page h1 {
      display: none !important;
    }
  }
  @-moz-document regexp(".*s=atlas.*") {
    #content > div[style^="text-align:center;"] {
      background-color: inherit !important;
      font-weight: bold !important;
    }
    #content > h1:nth-child(5) , .infotext.c {
      display: none !important;
    }
    #atlas td {
      vertical-align: top !important;
    }
    #atlas .c , p.c:nth-child(12){
      display: none !important;
    }
  }
  @-moz-document regexp(".*s=stats.*") {
    #content > div[style^="text-align:center;"] {
      background-color: inherit !important;
      font-weight: bold !important;
    }
    #content .infotext {
      display: none !important;
    }
  }
  
  @-moz-document regexp(".*p=archiv.*") {
    th.c:nth-child(3) {
      display: block !important;
      display: none !important;
    }
    .c.facebook-publish {
      display: none !important;
    }
  }
}
