css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.washingtontimes.com/rightread/"), url(http://washingtontimes.com/rightread/) {
  
  @font-face {
  font-family: 'Montserrat Alternates';
  font-style: normal;
  font-weight: 700;
  src: local('MontserratAlternates-Bold'), url(http://fonts.gstatic.com/s/montserratalternates/v4/YENqOGAVzwIHjYNjmKuAZhqxxyO8TqgHUo835u8oy092IY20qb3OO3nusUf_NB58.ttf) format('truetype');
}
  
  #mainlogo, #social-share-below  {
    display: none !important;
  }
  
 h1, p a {
  font-family: 'Montserrat Alternates' !important;
  font-style: normal !important;
  font-weight: 700 !important;
 } 
  
 body, #main, div, header#masthead, header#top-bar {
   background: #353535 !important;
 }
 
  p {
    color: #585656 !important;
  }
  
.equal-heights-column-inner {
  border-right: none !important;
}
  
.content-inner p {
  padding-left: 5px !important;
  border-left: 4px solid rgba(243, 231, 0, 0.6) !important;
  outline: transparent !important;
  transition: all 0.3s !important;
}
.content-inner p:hover,
.content-inner p:focus {
  border-color: transparent !important;
  background-color: rgba(52,73,94,0.1) !important;
  outline: 2px solid rgba(243, 231, 0, 0.7) !important;
} 
  
.content-inner p a:hover,
.content-inner p a:focus {
  color: #00F318 !important;
  text-decoration: none !important;
}

  h1 a, a {
      color: hsl(0, 0%, 55%) !important;
      transition: background-color 0.3s, border-color 0.3s, color 0.3s !important;
  }
  h1 a:hover, h1 a:focus, a:hover {
    color: #00F318 !important;
    text-decoration: none !important;
    
  }
  
  #secondary > div:nth-child(1) > div:nth-child(1) > div:nth-child(1), #primary > div:nth-child(1) > div:nth-child(1) > div:nth-child(1), div.rightread-photo:nth-child(2) {
    border-top: 9px solid rgba(243, 231, 0, 0.7)  !important;
  }
  
  
}
