css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



@media not all and (-moz-windows-glass) 
    
{
          sidebarheader:not(:-moz-lwtheme),
          #navigator-toolbox > *:not(#TabsToolbar):not(#toolbar-menubar):not(:-moz-lwtheme),
          #browser-bottombox:not(:-moz-lwtheme),
          #main-window[tabsontop="false"] :-moz-any(#TabsToolbar, #toolbar-menubar):not(:-moz-lwtheme),
          #main-window[chromehidden=""][tabsontop="false"]:not(:-moz-lwtheme),
          #main-window findbar 
        
          {

             background-color: RGB(212, 208, 200) !important;
                  
          }
}

#toolbar-menubar #menubar-items

{
    background:RGB(222, 218, 210);
    border-radius: 5px !important;
    box-shadow: inset grey 0px 0px 28px -13px;
    border: 1px solid #DED9D7;
    margin:0px 1px 0px 1px;
    padding: 2px;
    background-color: #E6E1DF !important;
    transition: all 0.4s ease-in-out;
}

#toolbar-menubar #menubar-items:hover

{
    background-color: #EDE8E6 !important;
}


toolbarbutton.bookmark-item:hover 

{
    transition: all 0.07s ease-in-out;
    background-color: #E6E1DF !important;
    border-radius: 4px;
    border-width: 1px !important;
    box-shadow: inset white 0px 1px, inset white 0px 1px 5px -2px, inset #B3ACAA 0px -12px 22px -23px !important;
    border-color: #A3A3A3 !important;
    text-shadow: white 1px 1px 1px;
    padding: 0px 3px 0px 3px !important;
}

toolbarbutton.bookmark-item:active

{
    padding: 2px 3px 0px 3px !important;
    background-color:white!important;
}

toolbarbutton.bookmark-item 

{
   -moz-appearance:none !important;
    padding: 0px 3px 0px 3px !important;
}

.tabbrowser-tabs

{
    margin-top:-1px;
}

.tabbrowser-tab[selected]

{
    box-shadow: white -10px 0px 10px -5px;
   
}

.tabbrowser-tabs > tab[selected], .tabbrowser-tabs > tab[selected]:hover, tab

{
    padding:3px 0px 5px 0px !important;   
}

.tab-close-button

{
    padding:10px 12px 10px 10px;margin:-10px;
    cursor: pointer;
    transition: all 0.01s ease-in-out;
}

.tab-close-button:hover,
.tab-close-button:hover[selected="true"]

{
   -moz-image-region: rect(0, 48px, 16px, 32px) !important;
}
