css:
@-moz-document domain(app.asana.com) {

body {
	font-family: IranSans, Tahoma !important;
}
.center-pane-lower-body {
	font-family: IranSans, Tahoma;
	font-weight: normal;
}
td.grid-cell.grid_cell_string {
	text-align: left;
}
td.grid-cell.grid_cell_string.non_insert_mode {
	font-size: 12px;
	padding-top: 5px;
	line-height: 1;
}
td.grid-cell.grid_cell_string.insert_mode {
	padding-top: 3px;
	font-size: 13px;
}

.in-assigned-to-me-new-task {
	font-weight: normal !important;
}
.in-assigned-to-me-new-task .grid_cell_string .task-row-text-input, .in-assigned-to-me-new-task .grid_cell_string .grid-subtask-shadow-text {
    font-weight: normal !important;
}
.grid-uncompact .grid_cell_string .task-row-text-input {
    direction: rtl;
    text-align: left;
}
.notification-header .notification-title {
	font-weight: normal !important;
	line-height: 1.7;
	font-size: 14px !important;
	direction: rtl;
	text-align: right;
}


}
