css:
@-moz-document url-prefix('https://www.tumblr.com/') {

/*
  Editor:
  Post title and content
*/
.post-content .title-field,
.post-content .caption-field,
.tag-input-wrapper {
  border: 1px solid lightblue;
  padding: 5px;
}
.post-form--tag-editor {
  margin-top: 0;
  padding-top: 0;
}
.post-form--tag-editor > .tag-input-wrapper {
  width: auto;
  display: block;
  border-top-width: 0;
}
.post-content .title-field .editor {
  min-height: 0 !important;
}

/*
  Menu:
  Text attributes and formatting
*/
.post-content .caption-field {
    margin-top: 47px;
}
.caption-field .bubbles {
    position: absolute !important;
    left: 0!important; top: -42px !important;
    display: block !important;
    /*border: 1px solid olive;*/
}
.caption-field .bubbles > * {
    opacity: 1 !important;
    transform: none !important;
}

/*
  Menu:
  Insert photo, video, horizontal line, read-more ellipsis
*/
/*
.caption-field .inline-controls {
    top: -37px !important; left: 350px !important;
    display: block !important;
}
.caption-field .inline-controls .opener {
     color: purple !important;
     display: none;
}
.caption-field .inline-controls .tray {
     color: purple !important;
     display: block !important;
}
*/

/* typography */
.post-form--form.post-content {  
    font-size: 16px !important;
    line-height: 1.17 !important; /* 42px/36px */    
}

.post-content .title-field *,
.post-content .caption-field .editor {
    font-size: inherit !important;
    line-height: inherit !important;
}

/*
  Dialog buttons:
  Post on the left, Close on the right;
*/
.post-form--bottom .controls-container .control {
    width: auto;
    display: inline-block;
}
.post-form--bottom .controls-container .control.left {
  float: right;
}

}
