css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("free.qidian.com") {
   .chapter #content{
	-moz-user-select: auto; /*火狐*/
	-webkit-user-select: auto; /*webkit浏览器*/
	-ms-user-select: auto; /*IE10*/
	-khtml-user-select: auto; /*早期浏览器*/
	user-select: auto;
}


}
@-moz-document domain("read.qidian.com") {
   #readcontent{
	-moz-user-select: auto !important; /*火狐*/
	-webkit-user-select: auto  !important; /*webkit浏览器*/
	-ms-user-select: auto !important; /*IE10*/
	-khtml-user-select: auto !important; /*早期浏览器*/
	user-select: auto !important;
}
}
