@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.tumblr.com") {
/* So, I like rainbows. */
div.post_body blockquote {
  padding-left: 8px !important;
  margin-left: 5px !important;
  border-left-width: 2px !important;
}
div.post_body > blockquote {
  border-left-color: darkviolet !important;
}
div.post_body > blockquote > blockquote {
  border-left-color: blue !important;
}
div.post_body > blockquote > blockquote > blockquote {
  border-left-color: turquoise !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote {
  border-left-color: green !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: yellow !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: orange !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: red !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: hotpink !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: darkviolet !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: blue !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
  border-left-color: turquoise !important;
}
div.post_body > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote > blockquote {
