css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){



.new_post_label i {
 top: 0px;
height: 60px;
-webkit-transition-property: top;
-moz-transition-property: top;
-ms-transition-property: top;
-o-transition-property: top;
transition-property: top;
-webkit-transition-duration: .08s;
-moz-transition-duration: .08s;
-ms-transition-duration: .08s;
-o-transition-duration: .08s;
transition-duration: .08s;
-webkit-transition-timing-function: linear;
-moz-transition-timing-function: linear;
-ms-transition-timing-function: linear;
-o-transition-timing-function: linear;
transition-timing-function: linear;
-webkit-transition-delay: 0s;
-moz-transition-delay: 0s;
-ms-transition-delay: 0s;
-o-transition-delay: 0s;
transition-delay: 0s;
}
 
  
.icon_post_text:before, .icon_post_photo:before, .icon_post_quote:before, .icon_post_link:before, .icon_post_chat:before, .icon_post_audio:before, .icon_post_video:before {
content: none;
}
    
 
.new_post_label i.icon_post_text {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) -1% 0;
}

    
.new_post_label i.icon_post_photo {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) 16.5% 0;
}
 

.new_post_label i.icon_post_quote {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) 33.75% 0;
}

.new_post_label i.icon_post_link {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) 51% 0;
}

.new_post_label i.icon_post_chat {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) 68.2% 0;
}

.new_post_label i.icon_post_audio {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) 85.2% 0;
}

.new_post_label i.icon_post_video {
background: url(https://36.media.tumblr.com/57604ff8c75a3e4f117060013c86984c/tumblr_nj0coqQCtL1t4oa2fo1_500.png) 103% 0;
}


.new_post_label:hover i.icon_post_photo, .new_post_label.hover i.icon_post_photo, .new_post_label:hover i.icon_post_quote, .new_post_label.hover i.icon_post_quote, .new_post_label:hover i.icon_post_text, .new_post_label.hover i.icon_post_text, .new_post_label:hover i.icon_post_link, .new_post_label.hover i.icon_post_link, .new_post_label:hover i.icon_post_chat, .new_post_label.hover i.icon_post_chat, .new_post_label:hover i.icon_post_audio, .new_post_label.hover i.icon_post_audio, .new_post_label:hover i.icon_post_video, .new_post_label.hover i.icon_post_video {
top: -5px;
}


}
