css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

@-moz-document url("chrome://browser/content/browser.xul") {
  #bookmarks-menu-button > dropmarker > _moz_generated_content_before {
    display: none!important;
  }

  #bookmarks-menu-button > toolbarbutton {
    display: none!important;
  }

  #bookmarks-menu-button > dropmarker:before {
    display: none!important;
  }

  #bookmarks-menu-button > dropmarker > image {
    border-radius: 2px!important;
  }
  
  #BMB_bookmarksPopup > menu,
  #BMB_bookmarksPopup > menuitem,
  #BMB_bookmarksPopup > menuseparator {
    display: none;
  }
  
  #BMB_bookmarksPopup > menu:not([id*='BMB']), #BMB_bookmarksShowAll {
    display: block!important;
  }
}
