css:
/*LOG DARK THEME - By Comentarinformal*/
@-moz-document url-prefix("http://www.gcinema.net/logs"),
url-prefix("http://www.gcinema.net/logs"),
url-prefix("https://www.gcinema.net/logs"), 
url-prefix("http://gcinema.net/logs"),
url-prefix("https://www.gcinema.net/logs"){

body{background: linear-gradient(to bottom, #45484D 0%, #000 100%) repeat scroll 0% 0% transparent !important;color:white !important}
#UserBox{color:black !important}
#userBox a{color:blue !important}
#menu_filter{background-color: #2A2A18 !important;border: 2px solid #715C00 !important}
select{background-color: #000 !important;color: #FFF !important;border: 2px solid #000 !important}
input{color: #FFF !important;background-color: #000 !important;border: 2px solid #808080 !important}
.event{color:black !important}
.event:not(.played):not(.queue){background: linear-gradient(to bottom, #45484D 0%, #000 100%) repeat scroll 0% 0% transparent !important;color:white !important;}
.event a{color:blue !important}
.event:not(.played):not(.queue) a{color:cyan !important}
.at{color:#B69D71 !important}
.event{box-shadow: 5px 5px 9px #1B1B1B !important}
}
